    float: right;
}

@media screen and (max-width: 330px) {
    .logo {
        float: none;
    }
}

.var
{
    color: #66C9EE;
}

.ya
{
    color: #82B964;
}

@-webkit-keyframes twinkly {
    0%   { background: #D24D33; }
    100% { background: lightgrey; }
}
@-moz-keyframes twinkly {
    0%   { background: #D24D33; }
    100% { background: lightgrey; }
}

.cursor
{
    width: 3px;
    height: 45px;
    margin-bottom: -10px;
    display: inline-block;
    -webkit-animation: twinkly .5s alternate infinite;
    -moz-animation: twinkly .5s alternate infinite;
}

.switcher
{
    display: block;
    padding: 0;
    margin: .5em 0 0;
    list-style: none;
    float: right;
}
.lang
{
    margin: 0;
    padding: 0;
    list-style: none;
    display: inline-block;
    width: 24px;
    height: 24px;
}

.socialIco
{
    padding-top: 2em;

    margin: 0;
    padding: 0;

    list-style: none;
}

@media screen and (max-width: 768px) {
    .socialIco
    {
        padding-top: 1em;
    }

    .item
    {
        display:inline-block;
    }
}

.feeds
{
    margin: 0;
    padding: 0;

    list-style: none;

    float: left;
    line-height: 1.25em;
    font-size: .8em;
    padding-top: .3em;
}

.ico
{
    width: 44px;
    height: 44px;
    display: block;
    margin: .5em 0 0 auto;
    border-radius: 50%;
    background-position: 50% 50%;
}

@media screen and (max-width: 768px) {
    .ico
    {
        margin: 0;
        margin-right: 0.5em;
    }
}

.rss
{
    composes: ico;
    background-image: url('./social-ico__rss.png');
}

@media screen and (max-width: 768px) {
    .rss
    {
        margin-right: 2em;
    }
}

.github
{
    composes: ico;
    background-image: url('./social-ico__github.png');
}

.twitter
{
    composes: ico;
    background-image: url('./social-ico__twitter.png');
}

.facebook
{
    composes: ico;
    background-image: url('./social-ico__facebook.png');
}

.linkedin
{
    composes: ico;
    background-image: url('./social-ico__linkedin.png');
}

@media screen and (max-width: 768px) {
    .feed
    {
        float: right;
        margin-right: 1em;
    }
    .feed .item
    {
        display: list-item;
    }
}

.rssText
{
    color: #DF620E;
}

